#!/bin/bash\n\nLOGFILE="sample.log"\nTEMP_LOG="${LOGFILE}.1"\n\n# Create a hard link to preserve the current data and allow writes to continue uninterrupted\nln "$LOGFILE" "$TEMP_LOG"\n\n# Truncate the original log file or remove it if preferred\n> "$LOGFILE"\n# or use: rm "$LOGFILE"\n\n# Optionally, compress and archive the old log\nbzip2 -c "$TEMP_LOG" > "${TEMP_LOG}.bz2" &\n\n# Remove the hard link after processing\nrm "$TEMP_LOG"