#!/bin/bash\n\n# Loop through every file in the current directory\nfor oldname in *; do\n    # Check if it's a regular file to avoid directories\n    if [[ -f "$oldname" ]]; then\n        # Create a new filename with capitalized first letter\n        newname=$(echo "$oldname" | sed 's/^\(.\)/\U\1/')\n        \n        # Ensure the new name is different from the old name\n        if [[ "$newname" != "$oldname" ]]; then\n            svn mv "$oldname" "$newname"\n        fi\n    fi\ndone\n\n# Commit the changes after all files have been renamed\nsvn commit -m "caps"