#!/bin/bash\n\n# Path to your certificate\nCERT_FILE="your_certificate.crt"\n\n# Extract SHA256 fingerprint from the certificate\nCERT_FINGERPRINT=$(keytool -printcert -file "$CERT_FILE" | grep "SHA256 Fingerprint" | cut -d" " -f3-)\n\n# List all certificates in Java's truststore and extract fingerprints\nTRUSTSTORE="$JAVA_HOME/jre/lib/security/cacerts"\nkeytool -list -keystore "$TRUSTSTORE" -v -storepass changeit | grep "SHA256 Fingerprint" | cut -d" " -f3- |\nwhile read TRUSTED_FINGERPRINT; do\n    if [ "$CERT_FINGERPRINT" == "$TRUSTED_FINGERPRINT" ]; then\n        echo "Certificate is trusted."\n        exit 0\n    fi\ndone\n\necho "Certificate is not trusted."\nexit 1