#!/bin/bash\n\n# Wait until the build completes\nwait_for_build() {\n    # This should be your build command, e.g., ./gradle build\n    echo "Build in progress..."\n    sleep 30s  # Simulate build time\n}\n\n# Fetch a random LeetCode problem\nfetch_problem() {\n    local problem_id=$((RANDOM % 1501))\n    local problem_data=$(curl -s https://leetcode.com/api/problems/all/)\n    local problem_slug=$(echo "$problem_data" | jq --arg pid "$problem_id" '.stat_status_pairs[$pid|tonumber].stat.question_title_slug')\n    local problem_url="https://leetcode.com/problems/${problem_slug//\"/}"\n    echo "Review this LeetCode problem while you wait: $problem_url"\n}\n\nwait_for_build\nfetch_problem