# First uninstall any existing installations via PECL\n   pecl uninstall pdo pdo_mysql\n\n   # Download specific versions for PHP 5.1.6\n   wget http://pecl.php.net/get/PDO-1.0.3.tgz\n   wget http://pecl.php.net/get/PDO_MYSQL-1.0.2.tgz\n   \n   tar -xzf PDO-1.0.3.tgz\n   cd PDO-1.0.3\n   phpize\n   ./configure --with-php-config=/usr/bin/php-config\n   make\n   sudo make install\n\n   # Repeat for PDO_MYSQL\n   cd ..\n   tar -xzf PDO_MYSQL-1.0.2.tgz\n   cd PDO_MYSQL-1.0.2\n   phpize\n   ./configure --with-php-config=/usr/bin/php-config --with-pdo-mysql=mysqlnd\n   make\n   sudo make install