#!/bin/sh\n\n# Compile the program\ngcc helloworld.c -o helloworld 2> error.log\n\n# Check if gcc encountered any errors or warnings\nif [ $? -ne 0 ]; then\n    # gcc failed to compile (errors)\n    echo "Failure"\n    cat error.log\nelif [ -s error.log ]; then\n    # gcc compiled with warnings\n    echo "Success with warnings"\n    cat error.log\nelse\n    # gcc compiled successfully without any errors or warnings\n    echo "Success!"\nfi\n\n# Optionally, remove the log file if no longer needed\nrm -f error.log