# Create a dedicated user for Apache if not already created\nsudo adduser --system --no-create-home --shell /bin/false apache_writer\n\n# Set the owner of the document root to this user for necessary directories\n# Replace /var/www/html/subdir with the directory where writing is allowed\nsudo chown -R apache_writer:www-data /var/www/html/subdir\nsudo chmod 2750 /var/www/html/subdir\n\n# Ensure other parts of the docroot are not writable\nsudo find /var/www/html/ ! -path /var/www/html/subdir -type d -exec chmod 550 {} \;