#!/bin/bash\n\nAPI_KEY="your_api_key"\nPHOTOSET_ID="photoset_id_here"  # This is your collection Y, but as Photoset ID\nNUMBER_OF_PHOTOS=5              # Your X value for number of photos to fetch\n\ncurl "https://api.flickr.com/services/rest/?method=flickr.photosets.getPhotos&api_key=$API_KEY&photoset_id=$PHOTOSET_ID&per_page=$NUMBER_OF_PHOTOS&format=json&nojsoncallback=1" | jq '.photoset.photo[] | {url: "https://farm\(.farm).staticflickr.com/\(.server)/\(.id)_\(.secret).jpg", caption: .title._content}'