#!/bin/bash\n\n# Find all .txt files matching the pattern\nfor file in *.txt; do\n    # Extract the datetime part from the filename\n    datetime=$(echo "$file" | grep -oP '^\d{14}')\n    \n    if [ ! -z "$datetime" ]; then\n        # Convert datetime string to a format recognized by touch\n        new_mtime=$(date -d "${datetime:0:4}-${datetime:4:2}-${datetime:6:2} ${datetime:8:2}:${datetime:10:2}" +"%Y%m%d%H%M.%S")\n        \n        # Change the file's mtime\n        touch -t "$new_mtime" "$file"\n    fi\ndone