#!/bin/bash\n\n# Variables\nDATE=$(date +"%F")\nBACKUP_DIR="/path/to/backups"\nDB_NAME="your_database_name"\nUSER="postgres"\nPASSWORD="your_password" # Consider using .pgpass for authentication\n\n# Create backup directory if it doesn't exist\nmkdir -p $BACKUP_DIR\n\n# Perform pg_dump with compression and place the file in the designated backup directory\nPGPASSWORD=$PASSWORD pg_dump -U $USER -d $DB_NAME | gzip > "$BACKUP_DIR/${DB_NAME}_backup_$DATE.sql.gz"