#!/bin/bash\n\nfor developer in $(seq 1 8); do\n   echo "Processing Developer $developer"\n   # Assuming each working copy is named wc_devN where N is the developer number\n   WC="wc_dev$developer"\n   BASE_WC="imported_wc"\n\n   for file in "$WC"/*; do\n      if [[ -f "$file" ]]; then\n         filename=$(basename "$file")\n         base_file="$BASE_WC/$filename"\n         \n         if [[ ! -e "$base_file" || $(diff "$file" "$base_file") ]]; then\n            echo "Difference found in $filename, updating..."\n            cp "$file" "$BASE_WC/"\n         fi\n      fi\n   done\ndone