# Install Ruby (required by Redmine)\nsudo apt-get install -y ruby-full\n\n# Install dependencies\nsudo apt-get install -y build-essential zlib1g-dev libyaml-dev \\n  libssl-dev libgdbm-dev libreadline-dev libncurses5-dev libffi-dev \\n  curl openssh-server checkinstall libxml2-dev libxslt-dev \\n  libcurl4-openssl-dev python-software-properties libmysqlclient-dev\n\n# Install additional packages\nsudo apt-get install -y apache2 libapache2-mod-passenger\n\n# Download and extract Redmine\nwget http://www.redmine.org/releases/redmine-5.0.0.tar.gz\ntar xvfz redmine-5.0.0.tar.gz\nmv redmine-5.0.0 /opt/redmine\n\n# Install gems required by Redmine\ncd /opt/redmine\nbundle install --without development test rmagick\n\n# Setup database (MySQL example)\nmysql -u root -p <<MYSQL_SCRIPT\nCREATE DATABASE redmine CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci;\nCREATE USER 'redmine'@'localhost' IDENTIFIED BY 'your_password';\nGRANT ALL PRIVILEGES ON redmine.* TO 'redmine'@'localhost';\nFLUSH PRIVILEGES;\nEXIT;\nMYSQL_SCRIPT\n\n# Configure database connection\ncp config/database.yml.example config/database.yml\nsed -i "s/username: root/username: redmine/g" config/database.yml\nsed -i "s/password: \"/password: your_password/g" config/database.yml\n\n# Generate the session store secret\nbundle exec rake generate_secret_token\n\n# Run database migrations\nRAILS_ENV=production bundle exec rake db:migrate\n\n# Load default data (optional)\nRAILS_ENV=production REDMINE_LANG=en bundle exec rake redmine:load_default_data