# Assume we want to extract data from table1 and name the output file based on field2\nmysql -u username -p -e "SELECT t1.field2 FROM table1 t1 LIMIT 1" database_name | while read filename; do\n    mysql -u username -p -e "SELECT * FROM table1 WHERE field2='$filename' INTO OUTFILE '/my_path/${filename}.txt' FIELDS TERMINATED BY ',' OPTIONALLY ENCLOSED BY '\"' LINES TERMINATED BY '\n';" database_name\ndone