#!/bin/bash\n\nFILE_PATH="/path/to/your/file"\n\n# Get the last modification time in seconds since epoch\nlast_modified=$(stat -c %Y "$FILE_PATH")\n\n# Get the current time in seconds since epoch\ncurrent_time=$(date +%s)\n\n# Calculate the difference in minutes\ndifference=$(( (current_time - last_modified) / 60 ))\n\nif [ $difference -le 3 ]; then\n    echo "File was modified within the last three minutes."\n    exit 0\nelse\n    echo "File was not modified within the last three minutes. Continuing script..."\n    # Continue with other operations\nfi