# Create directories to extract .o files\nmkdir -p temp_a temp_b\n\n# Extract .o files from the first shared library\ncd temp_a\nar x ../a.so\ncd ..\n\n# Extract .o files from the second shared library\ncd temp_b\nar x ../b.so\ncd ..\n\n# Combine all .o files from both directories into a single directory or command line\ngcc -shared -Wl,--whole-archive temp_a/*.o temp_b/*.o -Wl,--no-whole-archive -o c.so\n\n# Clean up the temporary directories if necessary\nrm -r temp_a temp_b