#!/bin/bash\n\n   USER_HOME=$(echo $HOME)\n\n   # Set the necessary SSL key and trust store properties\n   JAVA_OPTS="-Djavax.net.ssl.keyStore=${USER_HOME}/user.p12 \\n              -Djavax.net.ssl.keyStoreType=PKCS12 \\n              -Djavax.net.ssl.keyStorePasswordFile=${USER_HOME}/password.txt \\n              -Djavax.net.ssl.trustStore=/central/ca.jks"\n\n   # Launch Eclipse with the specified properties\n   ${ECLIPSE_HOME}/eclipse $JAVA_OPTS "$@"