#!/bin/bash\n\n# Compile untrusted Java code (assuming it's in UntrustedCode.java)\njavac UntrustedCode.java -d out/\n\n# Create a Dockerfile named Dockerfile.memory_limited\necho 'FROM openjdk:17-jre-slim\nWORKDIR /app\nCOPY out/ .\nCMD ["java", "-Xmx50m", "UntrustedCode"]' > Dockerfile.memory_limited\n\n# Build the Docker image\ndocker build -t untrusted-code-memory-limited -f Dockerfile.memory_limited .\n\n# Run the Docker container with memory constraints\ndocker run --memory="128m" --cpus="0.5" untrusted-code-memory-limited