#!/bin/bash\n\nexit_code=0  # Initialize a variable to keep track of the overall exit status\n\nfor i in $(seq 0 9); do\n    doCalculations $i &\n    pids[$i]=$!  # Store the process ID of the last command started by &\ndone\n\nfor pid in "${pids[@]}"; do\n    wait "$pid" || exit_code=$?  # Wait for each subprocess to finish and capture its exit code\n    if [ $exit_code -ne 0 ]; then\n        break  # Exit early if any subprocess fails\n    fi\ndone\n\nexit $exit_code  # Exit with the overall status, non-zero if any subprocess failed