#!/bin/bash\n\n# Set variables\nSOURCE="/home/yourusername"\nDESTINATION="/path/to/backup"\nBACKUP_DIR="$DESTINATION/latest"\nINCREMENTAL_DAILY="$DESTINATION/incremental_daily"\n\n# Create incremental backups using hard links to reduce disk space usage\nif [ -d "$BACKUP_DIR" ]; then\n    rsync -a --link-dest="$INC_BACKUP_DIR" "$SOURCE/" "$BACKUP_DIR/"\nelse\n    rsync -a "$SOURCE/" "$BACKUP_DIR/"\nfi\n\n# Store the current backup as last incremental\ncp -al "$BACKUP_DIR" "$INCREMENTAL_DAILY"\n\n# Optional: Maintain a set number of previous backups (e.g., 7 daily, 4 weekly)\nfind $DESTINATION/incremental_daily* -maxdepth 1 -type d -mtime +7 | xargs rm -rf