#!/bin/bash\n\n# Directory containing the files\nDIR="/path/to/your/directory"\n\n# Change to the directory\ncd "$DIR" || exit\n\n# Loop through each file matching the pattern\nfor file in Prefix_*.tex; do\n    # Extract the date part of the filename using sed\n    OLD_NAME=$(echo "$file" | grep -oP 'Prefix_\K\d{6}\.tex')\n    DATE_DDMMYY=${OLD_NAME:0:2}${OLD_NAME:2:2}${OLD_NAME:4:2}\n    \n    # Create new name with format yymmdd\n    NEW_DATE="${DATE_DDMMYY:4:2}${DATE_DDMMYY:2:2}${DATE_DDMMYY:0:2}"\n    NEW_NAME="Prefix_${NEW_DATE}.tex"\n    \n    # Rename the file\n    mv -- "$file" "$NEW_NAME"\ndone\n\necho "Renaming completed."