#!/bin/bash\n\n# Variables\nORACLE_HOME=/path/to/oracle/home\nexport ORACLE_HOME\nexport PATH=$ORACLE_HOME/bin:$PATH\n\nSQLPLUS="/usr/local/bin/sqlplus / as sysdba"\nRMAN="/usr/local/bin/rman target /"\n\n# Backup Function\ndo_backup() {\n    echo "Backing up the database..."\n    $RMAN <<EOF\nBACKUP DATABASE;\nEXIT;\nEOF\n}\n\n# Restore Function\ndo_restore() {\n    echo "Restoring the database to the last backup..."\n    $SQLPLUS <<EOF\nSHUTDOWN IMMEDIATE;\nSTARTUP MOUNT;\nEXIT;\nEOF\n\n    $RMAN <<EOF\nRUN {\n  RESTORE DATABASE;\n  RECOVER DATABASE;\n}\n\nEXIT;\nEOF\n\n$SQLPLUS <<EOF\nALTER DATABASE OPEN RESETLOGS;\nEXIT;\nEOF\n}\n\n# Uncomment the function you want to run: backup or restore\ndo_backup\n# do_restore