#!/bin/bash\n\n# Twilio credentials\n ACCOUNT_SID="your_account_sid"\nAUTH_TOKEN="your_auth_token"\nFROM_NUMBER="+1234567890"  # Twilio number\nTO_NUMBER="+0987654321"   # Your phone number\n\n# Server URL and expected content\nSERVER_URL="http://yourserver.com/check"\nEXPECTED_CONTENT="expected_output"\n\n# Perform the GET request\nresponse=$(curl -s "$SERVER_URL")\n\n# Check if the response matches the expected content\nif [ "$response" != "$EXPECTED_CONTENT" ]; then\n    # Send an SMS alert using Twilio's REST API\n    curl -X POST "https://api.twilio.com/2010-04-01/Accounts/$ACCOUNT_SID/Messages.json" \\n    --data-urlencode "To=$TO_NUMBER" \\n    --data-urlencode "From=$FROM_NUMBER" \\n    --data-urlencode "Body=Alert: Server did not respond correctly." \\n    -u "$ACCOUNT_SID:$AUTH_TOKEN"\nfi