#!/bin/bash\n\n# Paths to executable files or directories containing the executables\nEXECUTABLES=("path/to/executable1" "path/to/executable2")\n\n# Run each executable under valgrind and capture the output\nfor exec in "${EXECUTABLES[@]}"\ndo\n  echo "Running Valgrind on $exec..."\n  valgrind --leak-check=full --show-leak-kinds=all --track-origins=yes --verbose --log-file="valgrind_report_$(basename $exec).txt" "$exec"\ndone\n\necho "Valgrind tests completed. Check the report files for details."