#!/bin/bash\n\n# Use Oracle Wallet for authentication or set environment variable for password\nuser="your_username"\npasswd="your_password"\n\n# Execute SQL query and capture the output\nshellvariable=$(sqlplus -S $user/\$passwd <<EOF\nSET HEADING OFF\nSET FEEDBACK OFF\nSELECT count(1) FROM table1;\nEXIT;\nEOF\n)\n\n# Trim any leading or trailing whitespace from the variable\nshellvariable=$(echo $shellvariable | xargs)\n\n# Check if the variable is not zero and perform actions\nif [ "$shellvariable" -ne 0 ]; then\n    echo "Count is greater than zero."\n    # Add your logic here, e.g., send an email notification\n    mail -s "SQL Alert" your_email@example.com <<< "The count in table1 is non-zero: $shellvariable"\nelse\n    echo "No records found."\nfi