#!/bin/bash\n\n# Path to your deployment directory where SVN checkout resides.\nDEPLOY_DIR=~/deployment/app-dir\n\n# Update the code base with the latest version from SVN.\ncd $DEPLOY_DIR && svn update\n\n# Exclude 'uploads' directory from being deleted in FTP upload process.\nrsync -av --exclude='uploads/' $DEPLOY_DIR/ /path/to/local/copy/for/deployment/\n\n# Use LFTP to connect via FTP and deploy the application, excluding uploads\nlftp -u user,password ftp.yourhost.com -e 'mirror -R -v --delete --no-perms --exclude=uploads /path/to/local/copy/for/deployment/ /remote/webroot/path/ && quit'