# Stop MySQL server (command may vary based on system)\nsudo systemctl stop mysql\n\n# Navigate to the MySQL data directory\ncd /var/lib/mysql/\n\n# Assuming your database name is 'your_database'\nmkdir -p your_database/\ncd your_database/\n\n# Copy your table files into the new directory\ncp /path/to/client.frm .\ncp /path/to/client.MYD .\ncp /path/to/client.MYI .\n\n# Repeat for all other tables\n\n# Set proper permissions and ownership on these files\nsudo chown -R mysql:mysql /var/lib/mysql/your_database/\nsudo chmod -R 640 /var/lib/mysql/your_database/\n\n# Start MySQL server (command may vary based on system)\nsudo systemctl start mysql