#!/bin/bash\n\n# Path to the installed GCC 4.1 binaries\nGCC41_PATH="/usr/local/gcc-4.1/bin"\n\n# Set CC and CXX environment variables\nexport CC="$GCC41_PATH/gcc"\nexport CXX="$GCC41_PATH/g++"\n\n# Set include and library paths explicitly\nexport CPPFLAGS="-I/usr/local/gcc-4.1/include -I/usr/local/gcc-4.1/lib/gcc/x86_64-unknown-linux-gnu/4.1.2/include"\nexport CFLAGS="-L/usr/local/gcc-4.1/lib -L/usr/local/gcc-4.1/lib/gcc/x86_64-unknown-linux-gnu/4.1.2"\nexport LDFLAGS="-L/usr/local/gcc-4.1/lib -lgcc_s"\n\n# Build your application\nmake clean\nmake