aws s3api put-bucket-policy --bucket your-bucket-name --policy '{\n    "Version": "2012-10-17",\n    "Statement": [\n        {\n            "Effect": "Allow",\n            "Principal": {"AWS": "arn:aws:iam::FlixCloud-Account-ID:user/FlixCloud-User"},\n            "Action": ["s3:GetObject", "s3:PutObject"],\n            "Resource": "arn:aws:s3:::your-bucket-name/*"\n        }\n    ]\n}'