#!/bin/bash\n\n# Define environments\nENV=$1 # This should be either 'dev' or 'prod'\n\nif [ "$ENV" != "dev" ] && [ "$ENV" != "prod" ]; then\n  echo "Usage: $0 dev|prod"\n  exit 1\nfi\n\n# Export branch to a new directory on the live server\nSVN_URL="http://your-svn-repo.com/path/to/branch"\nAPP_DEPLOY_DIR="/var/www/app/releases/"\n\n# Ensure the deployment directory is created\nmkdir -p "$APP_DEPLOY_DIR"\n\n# Checkout SVN repository into the release directory\nsvn export $SVN_URL "${APP_DEPLOY_DIR}latest"\n\n# Set environment-specific configurations\nln -fnsf "${APP_DEPLOY_DIR}latest/config.${ENV}.php" "${APP_DEPLOY_DIR}latest/config.php"\n\n# Optionally, process other files like .htaccess using sed or awk if needed\nif [ "$ENV" == "prod" ]; then\n  # Example command to modify or replace .htaccess rules in production\n  cp "${APP_DEPLOY_DIR}latest/.htaccess.prod" "${APP_DEPLOY_DIR}latest/.htaccess"\nfi\n\n# Symlink the new release\nln -fnsfT "${APP_DEPLOY_DIR}latest" /var/www/app/current\n\necho "Deployment to $ENV successful!"