#!/bin/bash\n\n# Create a temporary build directory\nBUILD_DIR="build"\nrm -rf $BUILD_DIR\nmkdir $BUILD_DIR\n\n# Copy all non-.svn files to the build directory\nrsync --exclude='.svn' -av ./ $BUILD_DIR/\n\n# Remove any other unnecessary files/dirs if needed\n# Example:\n# rm -f ${BUILD_DIR}/path/to/unnecessary_file.php\n\necho "Build complete. Contents of $BUILD_DIR are ready for upload."