#!/bin/bash\n\n# Variables\nSOURCE="/path/to/source"\nBACKUP_DIR="/path/to/backup/directory"\nREMOTE="remote-name:path/in/remote"\n\n# Set passphrase for GnuPG (optional, but recommended)\nexport PASSPHRASE='your-passphrase'\n\n# Incremental Backup with Duplicity\nduplicity \\n    --full-if-older-than 1M \\n    --exclude '/path/to/source/exclude-me' \  # Add exclusions as needed\n    $SOURCE \\n    "file://$BACKUP_DIR"\n\n# Sync backups to remote storage using Rclone\nrclone sync "$BACKUP_DIR" "$REMOTE"\n\n# Optional: Remove old backups (adjust as per your needs)\nduplicity remove-older-than 6M --force file://"$BACKUP_DIR"