# Example of a simple bash script to set the proper permission on new rev directories\n   # Place this in your hooks directory as post-commit, ensure it's executable\n\n   REPOS="$1"\n   REV="$2"\n\n   # Adjust the path if yours differs\n   REV_DIR="/svn/db/revs/$((REV / 1000))"\n\n   if [ -d "$REV_DIR" ]; then\n       # Ensure group write and sticky bit are set\n       chmod g+ws "$REV_DIR"\n   fi