#!/bin/sh\n\n# Define the branches to push automatically to the central server.\nAUTOPUSH_BRANCHES=("main" "develop")\n\n# Save current branch name.\nCURRENT_BRANCH=$(git rev-parse --abbrev-ref HEAD)\n# Iterate over branches we want to autopush, and check if current branch matches one of them.\nfor BRANCH in "${AUTOPUSH_BRANCHES[@]}"; do\n    if [ "$CURRENT_BRANCH" == "$BRANCH" ]; then\n        git push origin $CURRENT_BRANCH\n        exit 0\n    fi\ndone\n\nexit 0 # Do nothing for branches not included in AUTOPUSH_BRANCHES.