#!/bin/bash\n\n# Define the directory containing flat files\nFLAT_FILES_DIR="/path/to/flat/files"\n\n# Define the archive directory\nARCHIVE_DIR="/path/to/archive/directory"\n\n# Create date-stamped archive file\nDATE=$(date +"%Y%m%d")\nTAR_FILE="$ARCHIVE_DIR/flat_files_$DATE.tar.gz"\n\n# Archive and compress the files\ntar -czf "$TAR_FILE" -C "$FLAT_FILES_DIR" .\n\n# Optionally, remove original files after archiving (be cautious with this step)\nrm -f "$FLAT_FILES_DIR"/*\n\necho "Archived flat files into $TAR_FILE"