# Linux/Unix example:\nmkdir -p release/bundle/bin\ncp -a mycustomjre/* release/bundle/\ncp target/your-application.jar release/bundle/bin/\ncat <<EOF > release/bundle/bin/run.sh\n#!/bin/sh\ncd \$(dirname \$0)\n\$PWD/../lib/bin/java -m com.your.module/com.your.MainClass\nEOF\nchmod 755 release/bundle/bin/*\n\n# Windows example:\nmkdir release\bundle\bin\nxcopy mycustomjre /E release\bundle\\ncopy target\your-application.jar release\bundle\bin\\necho @call "..\lib\bin\java.exe" -m com.your.module/com.your.MainClass > release\bundle\bin\run.bat