#!/bin/bash\n# Create a timestamped directory for the build\nBUILD_DIR="./build_$(date +%Y%m%d_%H%M%S)"\n\n# Copy everything except the tests directory\nrsync -av --exclude='*.bzr' --exclude='/tests/' /path/to/project-root/ $BUILD_DIR/\n\n# Compress the build directory into a zip file\nzip -r ${BUILD_DIR}.zip $BUILD_DIR\n\n# Clean up if necessary\nrm -rf $BUILD_DIR\n\necho "Build created: ${BUILD_DIR}.zip"