# /etc/init/backup-usb-job.conf\ndescription "Handle backup USB insert"\n\nstart on backup-usb-inserted\nstop on shutdown\n\nscript\n    # Get the device node (e.g., /dev/sdb1)\n    DEVICE=$(udevadm info -q name --name-match='sd*' | tail -n 1)\n    \n    # Mount point\n    MOUNT_POINT="/mnt/backup"\n    \n    # Create mount point if it doesn't exist\n    mkdir -p $MOUNT_POINT\n    \n    # Mount the USB drive\n    mount /dev/$DEVICE $MOUNT_POINT\n    \n    # Copy files to the USB\n    cp /path/to/source/files $MOUNT_POINT\n    \n    # Unmount the USB drive after copying\n    umount $MOUNT_POINT\nend script