# Assuming all required jars are in a directory\njar_dir=/path/to/your/jars\n\n# Load each jar file, ensuring dependencies precede their dependants\nloadjava -verbose -schema MYUSER -user MYUSER/MYPWD@dbinstance -force $jar_dir/some-dependency-1.jar\nloadjava -verbose -schema MYUSER -user MYUSER/MYPWD@dbinstance -force $jar_dir/some-dependency-2.jar\n# Load saaj-impl last, or after all dependencies have been loaded\nloadjava -verbose -schema MYUSER -user MYUSER/MYPWD@dbinstance -resolve -force $jar_dir/saaj-impl.jar