#!/bin/bash\n\n# Deployment Script for CodeIgniter Project\n\n# Variables\nREPO="https://github.com/yourusername/your-codeigniter-project.git"\nBRANCH="main"\nTARGET="/var/www/html/live-site"\n\necho "Starting deployment process..."\n\n# Fetch latest changes from the repository\ngit fetch --all\ngit reset --hard origin/$BRANCH\n\n# Update Composer dependencies (if any)\ncomposer install --no-dev --optimize-autoloader -d $TARGET\n\n# Execute database migrations if necessary\nphp artisan migrate --force\n\n# Restart web server to clear caches and load new files\nsudo systemctl restart apache2\n\necho "Deployment completed successfully."