#!/bin/bash\n\n    BUG_ID=123456\n    COMMENT="New automated comment from script."\n    USERNAME="your-username"\n    PASSWORD="your-password"\n\n    curl -X POST \\n        --header 'Content-Type: text/xml' \\n        --data "<methodCall><methodName>Bug.add_comment</methodName><params>\n            <param>\n                <value>\n                    <struct>\n                        <member>\n                            <name>ids</name>\n                            <value><array><data><value><i4>${BUG_ID}</i4></value></data></array></value>\n                        </member>\n                        <member>\n                            <name>comment</name>\n                            <value><string>${COMMENT}</string></value>\n                        </member>\n                    </struct>\n                </value>\n            </param>\n        </params></methodCall>" \\n        "http://${USERNAME}:${PASSWORD}@localhost/bugzilla/xmlrpc.cgi"