#!/bin/bash\n\n# Directory containing your PDF files\nPDF_DIR="./path/to/pdfs"\n\n# Output directory for TIFF files\nTIFF_DIR="./path/to/tiffs"\nmkdir -p $TIFF_DIR\n\n# Loop through all PDFs in the source directory\nfor pdf_file in "$PDF_DIR"/*.pdf; do\n  # Convert each PDF to a 300 DPI TIFF in the output directory\n  tiff_file="$TIFF_DIR/$(basename "${pdf_file%.pdf}").tiff"\n  convert -density 300 "$pdf_file" "$tiff_file"\ndone