#!/bin/bash\n\n# Configuration\nMYSQL_USER="your_mysql_user"\nMYSQL_PASSWORD="your_mysql_password"\nDATABASE_NAME="your_database_name"\nBACKUP_DIR="/path/to/backup/directory"\nFTP_SERVER="ftp.example.com"\nFTP_USER="ftp_user"\nFTP_PASS="ftp_pass"\nDATE=$(date +%Y%m%d_%H%M%S)\n\n# Create backup directory if it doesn't exist\nmkdir -p $BACKUP_DIR\n\n# Backup MySQL database\nmysqldump --user=$MYSQL_USER --password=$MYSQL_PASSWORD $DATABASE_NAME > $BACKUP_DIR/db_backup_$DATE.sql\n\n# Compress the website files and store in the backup folder\ntar -czf $BACKUP_DIR/site_backup_$DATE.tar.gz /path/to/your/web/root/\n\n# Change to the backup directory for easier FTP process\ncd $BACKUP_DIR\n\n# Upload backup files via FTP\nftp -n $FTP_SERVER <<END_SCRIPT\nquote USER $FTP_USER\nquote PASS $FTP_PASS\nmkdir backups_$DATE\ncd backups_$DATE\nmput db_backup_$DATE.sql\nmput site_backup_$DATE.tar.gz\nquit\nEND_SCRIPT\n\n# Remove old backups (optional, uncomment lines below to cleanup)\n# find $BACKUP_DIR -type f -name "*.sql" -mtime +7 -exec rm {} \;\n# find $BACKUP_DIR -type f -name "*.tar.gz" -mtime +7 -exec rm {} \;\n\necho "Backup completed and uploaded successfully."