#!/bin/bash\n\nFILE="my_playlist.m3u"\n\n# Function to determine the OS type\nget_os_type() {\n    case "$OSTYPE" in\n      linux*)   echo "linux" ;;\n      cygwin|msys)  echo "windows" ;; # This helps with Windows/Cygwin environments\n      *)        echo "unknown" ;;\n    esac\n}\n\n# Get the OS type\nOS_TYPE=$(get_os_type)\n\ncase $OS_TYPE in\n    linux)\n        xdg-open "$FILE"\n        ;;\n    windows)\n        cygstart "$FILE"\n        ;;\n    *)\n        echo "Unrecognized OS, cannot determine default application."\n        exit 1\n        ;;\nesac