# Navigate to the directory where you have extracted PHP5 source code\n   cd /path/to/php-5.x.x\n\n   # Configure PHP5 with a different prefix and ini path\n   ./configure --prefix=/usr/local/php5 \\n               --with-config-file-path=/usr/local/php5/lib \\n               --enable-cli \\n               --enable-cgi \\n               # Add other options as necessary\n\n   # Compile and install PHP5\n   make && sudo make install