#!/bin/bash\nawk 'END {\n    # previous awk code...\n\n    Tb = (bhat - b0) / seb\n    Ta = (ahat - a0) / sea\n\n    pb_t=$(echo "$n-2 $Tb" | Rscript -e 'n <- scan(quiet=TRUE)[1]; t_stat <- scan(quiet=TRUE)[2]; 2 * pt(-abs(t_stat), df=n)')\n    pa_t=$(echo "$n-2 $Ta" | Rscript -e 'n <- scan(quiet=TRUE)[1]; t_stat <- scan(quiet=TRUE)[2]; 2 * pt(-abs(t_stat), df=n)')\n\n    print "p-value for beta: ", pb_t\n    print "p-value for alpha: ", pa_t\n\n    # finish script...\n}' data.dat