#!/bin/bash\n\n# Define the directory to search in (can be current or another path)\nDIR="."\n\n# Define the words that must all exist in a file to keep it\nWORDS=("word1" "word2" "word3")\n\n# Loop through each file in the specified directory\nfor FILE in "$DIR"/*; do\n    # Assume the file contains all words\n    CONTAINS_ALL=true\n    \n    # Check for each word\n    for WORD in "${WORDS[@]}"; do\n        if ! grep -qF "$WORD" "$FILE"; then\n            CONTAINS_ALL=false\n            break\n        fi\n    done\n    \n    # If the file does not contain all words, delete it\n    if [ "$CONTAINS_ALL" = false ]; then\n        rm -f "$FILE"\n        echo "Deleted $FILE because it did not contain all specified words."\n    fi\ndone\n\necho "Process completed."