# Create CA key and certificate\nopenssl genpkey -algorithm RSA -out ca.key -aes256 -pkeyopt rsa_keygen_bits:4096\nopenssl req -x509 -new -nodes -key ca.key -sha256 -days 3650 -out ca.crt\n\n# Create a client certificate (repeat for each allowed machine)\nopenssl genpkey -algorithm RSA -out client.key -aes256 -pkeyopt rsa_keygen_bits:2048\nopenssl req -new -key client.key -out client.csr\nopenssl x509 -req -in client.csr -CA ca.crt -CAkey ca.key -CAcreateserial -out client.crt -days 365 -sha256