# Initialize an empty git repo and enter it\nmkdir static_only && cd static_only\ngit init\n\n# Add remote origin pointing to your main repository\ngit remote add -f origin https://github.com/yourusername/yourrepository.git\n\n# Enable sparse-checkout\ngit config core.sparseCheckout true\n\n# Define the path of the folder you want to checkout\necho "static/" >> .git/info/sparse-checkout\n\n# This will fetch and checkout only the /static directory\ngit pull origin mai