#!/bin/bash\n\ncheck_query_syntax() {\n    local query=$1\n    local mysql_output\n    local error_code\n\n    # Try to prepare the SQL statement without executing it.\n    mysql_output=$(echo "PREPARE stmt FROM '$query'; DEALLOCATE PREPARE stmt;" | mysql -u your_username -p'your_password' -D your_database 2>&1)\n    error_code=$?\n\n    if [ $error_code -eq 0 ]; then\n        echo "Query is syntactically valid."\n    else\n        echo "Syntax Error: ${mysql_output}"\n    fi\n}\n\nquery="SELECT * FROM x;"\ncheck_query_syntax "$query"\n\nquery="SECLET * RFOM x;"\ncheck_query_syntax "$query"