#!/bin/bash\n\nORACLE_QUERY="SELECT COUNT(*) FROM your_table WHERE your_condition"\nORACLE_USER="your_user"\nORACLE_PASSWD="your_password"\nTEMP_FILE_PATH="/path/to/your/tmp/file"\nLOG_FILE="/path/to/logfile.log"\n\nwhile true; do\n    # Check if the row exists in Oracle table\n    ROW_EXISTS=$(echo "$ORACLE_QUERY" | sqlplus -S $ORACLE_USER/$ORACLE_PASSWD@your_database)\n\n    # Extract number from oracle output\n    ROW_COUNT=$(echo $ROW_EXISTS | awk '{print $1}')\n\n    if [ "$ROW_COUNT" -gt 0 ]; then\n        # Check if the file exists and log its attributes\n        if [ -f "$TEMP_FILE_PATH" ]; then\n            ls -l "$TEMP_FILE_PATH" >> "$LOG_FILE"\n        else\n            echo "File does not exist: $TEMP_FILE_PATH" >> "$LOG_FILE"\n        fi\n\n        sleep 5\n    else\n        # If row does not exist, exit the loop\n        echo "Row no longer exists in Oracle table. Exiting."\n        break\n    fi\ndone\n\n# Capture last state of the file if it still exists at the end\nif [ -f "$TEMP_FILE_PATH" ]; then\n    ls -l "$TEMP_FILE_PATH" >> "${LOG_FILE}_final_state"\nfi